mapper.select:
SELECT count(0)
FROM book_recode b
WHERE status != -1
               And id= #{id}
               And project = #{project}
               And device = #{device}
               And applicant = #{applicant}
                 And begin_time<= #{time} And end_time>= #{time}

SELECT
    id                                      id,
    project                                 project,
    device                                  device,
    applicant                               applicant,
    status                                  status,
    DATE_FORMAT(begin_time, '%Y.%m.%d %T')  beginTime,
    DATE_FORMAT(end_time, '%Y.%m.%d %T')    endTime,
    DATE_FORMAT(create_time, '%Y.%m.%d %T') createTime,
    DATE_FORMAT(update_time, '%Y.%m.%d %T') updateTime
FROM book_recode
WHERE status != -1
      And id= #{id}
      And project = #{project}
      And device = #{device}
      And applicant = #{applicant}
        And begin_time<= #{time} And end_time>= #{time} 
ORDER BY id
LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE book_recode
project=#{project}, device=#{device}, begin_time=#{beginTime}, end_time=#{endTime}, applicant=#{applicant}, status=#{status}
WHERE id = #{id}
