mapper.select:
SELECT count(0)
FROM device d
      LEFT JOIN lab l ON d.location = l.id
WHERE d.status != -1
And d.id= #{id}

               AND d.No like #{No}


               AND d.name like #{name}


               AND d.location = #{location}


               AND d.model like #{model}


               AND d.func like #{func}


               AND d.require_reputation like #{requireReputation}


               AND d.band like #{bands}


               AND d.status = #{status}

SELECT 
SUM(TIMESTAMPDIFF(second,d.create_time,NOW()))
greatest(SUM(TIMESTAMPDIFF(second,greatest(d.create_time,#{beginTime}),LEAST(NOW(),#{endTime} ))),0)          
FROM device d
 WHERE d.status != -1
And d.id= #{id}

               AND d.No like #{No}


               AND d.name like #{name}


               AND d.location = #{location}


               AND d.model like #{model}


               AND d.band like #{bands}


               AND d.status = #{status}

         SELECT
             d.id                                      id,
             d.No                                      No,
             d.name                                    name,
             d.func                                    func,
             d.require_reputation                      requireReputation,
             d.description                             description,
             d.location                                location,
             l.name                                    locationName,
             l.description                             locationDescription,
             l.location                                locationAddress,
             d.band                                    bands,
             d.model                                   model,
             d.status                                  status,
             DATE_FORMAT(d.create_time, '%Y/%m/%d %T') createTime,
             DATE_FORMAT(d.update_time, '%Y/%m/%d %T') updateTime
         FROM device d
              LEFT JOIN lab l ON d.location = l.id
         WHERE d.status != -1 And l.status != -1
And d.id= #{id}

               AND d.No like #{No}


               AND d.name like #{name}


               AND d.location = #{location}


               AND d.model like #{model}


               AND d.band like #{bands}


               AND d.func like #{func}


               AND d.require_reputation like #{requireReputation}


               AND d.status = #{status}

         ORDER BY d.id
         LIMIT #{offSet}, #{pageRow}

SELECT * FROM device d JOIN (( SELECT b.device id, IFNULL(SUM(TIMESTAMPDIFF(SECOND,b.begin_time,b.end_time)),0)*100/ SUM(TIMESTAMPDIFF(SECOND,d.create_time, NOW())) AS rate FROM book_recode b RIGHT JOIN device d ON d.id=b.device WHERE b.STATUS != -1 GROUP BY b.device) UNION ( SELECT d.id, 0 AS rate FROM device d WHERE d.id NOT IN ( SELECT device FROM book_recode WHERE STATUS !=-1) )) x ON x.id = d.id WHERE d.id NOT IN ( SELECT d.id FROM device d JOIN book_recode b ON d.id=b.device
(b.begin_time<= #{endTime} And b.end_time>= #{beginTime} )Or #{beginTime} < b.create_time
) ORDER BY rate,require_reputation LIMIT #{offSet}, #{pageRow}

mapper.update:
UPDATE device
name=#{name}, No=#{No}, description=#{description}, model=#{model}, `band`=#{bands}, location=#{location}, func = #{func}, require_reputation = #{requireReputation}, status=#{status}
WHERE id = #{id}

UPDATE device
SET status=-1
WHERE id = #{id}
